import requestsimport refrom kodi_six import xbmc, xbmcgui, xbmcaddonfrom six import PY2dialog = xbmcgui.Dialog()Headers = {'User-Agent' : 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/60.0.3112.113 Safari/537.36' }Notice = xbmc.LOGNOTICE if PY2 else xbmc.LOGINFOclass Scraper:    def __init__(self):        self.Base = ('https://www.filmxy.tv/%s')        self.Search = ('')        self.links = []    def Index(self,type, term,year,imdb):        year = year.strip()        if type == 'MOVIE':            try:                found = 0                Checker = term                MovieName = term                Term = term.replace(' ','-').replace(':','')                link = requests.get(self.Base %Term +'-'+year ,headers=Headers).text                sources = re.findall('''iframe\s+src=.*?(http.*?)&''',link,flags=re.DOTALL)                for i in sources:                    mtitle = ('FilmXY ( DIRECT )| FHD | %s' % MovieName)                    quality = '2'                    self.links.append({'title': mtitle, 'url': i, 'quality': quality, 'Debrid' : False, 'Direct' : True})                if len(self.links) < 1: xbmc.log("No Results From ::: FilmXY" , level=Notice)                 else: return self.links            except Exception as c:                xbmc.log("SCRAPER ERROR FILMXY  ::: %s" %c , level=Notice)        else: pass